import React, { useContext, useState, useEffect } from 'react';
import { 
  Container, 
  Typography, 
  TextField, 
  Button, 
  Stack, 
  CircularProgress,
  Alert,
  Box,
  Paper
} from '@mui/material';
import { useNavigate, useLocation } from 'react-router-dom';
import { TokenContext } from '../context/TokenContext';

const TokenForm = () => {
  const navigate = useNavigate();
  const { state: { gender, service } } = useLocation();
  const { addToken, verifyOTP, loading, error } = useContext(TokenContext);

  const [formData, setFormData] = useState({
    name: '',
    mobile: '',
    otp: ''
  });
  const [otpSent, setOtpSent] = useState(false);
  const [generatedOTP, setGeneratedOTP] = useState('');
  const [tokenData, setTokenData] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSendOTP = async () => {
    if (!formData.mobile || !formData.name) {
      setErrorMessage('Please enter both name and mobile number');
      return;
    }

    try {
      const { token, otp } = await addToken({
        gender,
        service,
        name: formData.name,
        mobile: formData.mobile
      });

      setTokenData(token);
      setOtpSent(true);
      setErrorMessage('');

      // Store the OP for display in the UI
      setGeneratedOTP(otp);

    } catch (err) {
      setErrorMessage(err.message || 'Failed to send OTP');
    }
  };

  const handleVerifyOTP = async () => {
    if (!formData.otp) {
      setErrorMessage('Please enter the OTP');
      return;
    }

    try {
      const verifiedToken = await verifyOTP(
        tokenData.id,
        formData.otp,
        gender
      );

      // Navigate to success page with token number
      navigate('/success', { 
        state: { 
          tokenNumber: verifiedToken.token_number,
          name: verifiedToken.name,
          service: verifiedToken.service,
          gender: verifiedToken.gender
        } 
      });

    } catch (err) {
      setErrorMessage(err.message || 'Invalid OTP. Please try again.');
    }
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 6 }}>
      <Typography variant="h5" gutterBottom align="center" sx={{ mb: 4 }}>
        {service} - {gender.charAt(0).toUpperCase() + gender.slice(1)}
      </Typography>

      <Paper elevation={3} sx={{ p: 3, borderRadius: 2 }}>
        <Stack spacing={3}>
          {errorMessage && (
            <Alert severity="error" onClose={() => setErrorMessage('')}>
              {errorMessage}
            </Alert>
          )}

          {!otpSent ? (
            <>
              <TextField
                label="Your Name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                fullWidth
                disabled={loading}
              />
              <TextField
                label="Mobile Number"
                name="mobile"
                type="tel"
                value={formData.mobile}
                onChange={handleChange}
                fullWidth
                disabled={loading}
                inputProps={{ maxLength: 10, pattern: '[0-9]*' }}
              />
              <Button
                variant="contained"
                onClick={handleSendOTP}
                disabled={!formData.name || !formData.mobile || loading}
                fullWidth
                size="large"
                sx={{ mt: 2 }}
              >
                {loading ? <CircularProgress size={24} /> : 'Send OTP'}
              </Button>
            </>
          ) : (
            <>
              <Box sx={{ mb: 2, p: 2, bgcolor: 'background.paper', borderRadius: 1, border: '1px dashed', borderColor: 'primary.main' }}>
                <Typography variant="body1" gutterBottom>
                  We've sent an OTP to {formData.mobile}
                </Typography>
                <Box sx={{ mt: 2, p: 2, bgcolor: 'grey.100', borderRadius: 1, textAlign: 'center' }}>
                  <Typography variant="h6" color="primary" gutterBottom>
                    Your OTP is: {generatedOTP}
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    (This is a test mode. In production, this would be sent via SMS)
                  </Typography>
                  <Button 
                    variant="outlined" 
                    size="small" 
                    onClick={() => {
                      navigator.clipboard.writeText(generatedOTP);
                      // You could add a snackbar/toast here to confirm copy
                    }}
                    sx={{ mt: 1 }}
                  >
                    Copy OTP
                  </Button>
                </Box>
              </Box>
              <TextField
                label="Enter OTP"
                name="otp"
                value={formData.otp}
                onChange={handleChange}
                fullWidth
                disabled={loading}
                inputProps={{ maxLength: 6, pattern: '[0-9]*' }}
              />
              <Box sx={{ display: 'flex', gap: 2 }}>
                <Button
                  variant="outlined"
                  onClick={() => setOtpSent(false)}
                  disabled={loading}
                  fullWidth
                >
                  Back
                </Button>
                <Button
                  variant="contained"
                  onClick={handleVerifyOTP}
                  disabled={!formData.otp || loading}
                  fullWidth
                >
                  {loading ? <CircularProgress size={24} /> : 'Verify & Get Token'}
                </Button>
              </Box>
            </>
          )}
        </Stack>
      </Paper>
    </Container>
  );
};

export default TokenForm;
