import React, { useContext } from 'react';
import { Container, Typography, Table, TableHead, TableRow, TableCell, TableBody, Button, Box, Paper, Chip } from '@mui/material';
import Layout from '../components/Layout';
import { TokenContext } from '../context/TokenContext';

const AdminPanel = () => {
  const { tokens, serveToken } = useContext(TokenContext);

  return (
    <Layout>
    <Container sx={{ mt: 6 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 4 }}>
        <Typography variant="h4" color="primary">
          Token Queue
        </Typography>
        <Button variant="contained" color="secondary">
          Refresh
        </Button>
      </Box>

      <Paper elevation={2} sx={{ p: 2, borderRadius: 3, overflow: 'hidden' }}>
        <Table>
        <TableHead>
          <TableRow>
            <TableCell>Token</TableCell>
            <TableCell>Name</TableCell>
            <TableCell>Service</TableCell>
            <TableCell>Mobile</TableCell>
            <TableCell>Status</TableCell>
            <TableCell>Action</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {tokens.map((t) => (
            <TableRow 
              key={t.tokenNumber} 
              sx={{ 
                '&:last-child td, &:last-child th': { border: 0 },
                '&:hover': { bgcolor: 'action.hover' }
              }}
            >
              <TableCell>{t.tokenNumber}</TableCell>
              <TableCell>{t.name}</TableCell>
              <TableCell>{t.service}</TableCell>
              <TableCell>{t.mobile}</TableCell>
              <TableCell>
                <Chip 
                  label={t.served ? 'Served' : 'Waiting'} 
                  color={t.served ? 'default' : 'primary'}
                  variant={t.served ? 'outlined' : 'filled'}
                />
              </TableCell>
              <TableCell>
                {!t.served && (
                  <Button 
                    variant="contained" 
                    size="small" 
                    onClick={() => serveToken(t.tokenNumber)}
                    disabled={t.served}
                    sx={{ 
                      minWidth: 100,
                      opacity: t.served ? 0.7 : 1
                    }}
                  >
                    {t.served ? 'Served' : 'Mark Served'}
                  </Button>
                )}
              </TableCell>
            </TableRow>
          ))}
          {tokens.length === 0 && (
            <TableRow>
              <TableCell colSpan={6} align="center">
                No tokens yet
              </TableCell>
            </TableRow>
          )}
        </TableBody>
        </Table>
      </Paper>
    </Container>
    </Layout>
  );
};

export default AdminPanel;
