import React, { useContext, useState, useEffect } from 'react';
import { TokenContext } from '../context/TokenContext';
import { 
  Container, 
  Typography, 
  Grid, 
  Button, 
  Paper, 
  Box, 
  IconButton,
  useMediaQuery,
  Chip,
  Fade
} from '@mui/material';
import { useTheme } from '@mui/material/styles';
import Layout from '../components/Layout';
import { useNavigate, useLocation } from 'react-router-dom';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';

const Home = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const navigate = useNavigate();
  const location = useLocation();
  const { getCurrentToken, getRecentToken } = useContext(TokenContext);
  const [animate, setAnimate] = useState(false);
  const [selectedGender, setSelectedGender] = useState(null);

  // Get current and recent tokens for both genders
  const currentMaleToken = getCurrentToken('male') || 0;
  const currentFemaleToken = getCurrentToken('female') || 0;
  const recentMaleToken = getRecentToken('male');
  const recentFemaleToken = getRecentToken('female');

  useEffect(() => {
    setAnimate(true);
    // Check if we're coming back from a service selection
    if (location.state?.fromService) {
      setSelectedGender(location.state.gender);
    }
  }, [location.state]);

  const handleSelect = (gender) => {
    setSelectedGender(gender);
  };

  const handleBack = () => {
    setSelectedGender(null);
  };

  const handleProceed = () => {
    if (selectedGender) {
      navigate('/services', { 
        state: { 
          gender: selectedGender,
          fromHome: true 
        } 
      });
    }
  };

  // Render gender selection screen
  const renderGenderSelection = () => (
    <Box sx={{ textAlign: 'center' }}>
      <Box sx={{ mb: 6, mt: 4 }}>
        <Typography variant="h3" component="h1" gutterBottom sx={{ 
          fontWeight: 700,
          background: 'linear-gradient(45deg, #33126B 30%, #FF0A91 90%)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          mb: 2
        }}>
          Welcome to Purple Salon
        </Typography>
        <Typography variant="h6" color="text.secondary" paragraph sx={{ mb: 4 }}>
          Select your gender to view token status
        </Typography>
      </Box>

      <Grid container spacing={3} justifyContent="center" sx={{ maxWidth: 900, mx: 'auto' }}>
        {/* Male Section */}
        <Grid item xs={12} sm={6} md={5}>
          <Paper 
            elevation={3} 
            onClick={() => handleSelect('male')}
            sx={{ 
              p: 4, 
              borderRadius: 4,
              cursor: 'pointer',
              transition: 'all 0.3s ease-in-out',
              '&:hover': {
                transform: 'translateY(-5px)',
                boxShadow: 6,
                borderColor: 'primary.main',
                borderWidth: '2px',
                borderStyle: 'solid'
              },
              bgcolor: 'rgba(33, 150, 243, 0.05)',
              height: '100%',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              textAlign: 'center',
              minHeight: 300
            }}
          >
            <ArrowBackIcon sx={{ fontSize: 80, color: 'primary.main', mb: 2 }} />
            <Typography variant="h4" color="primary" gutterBottom sx={{ fontWeight: 'bold' }}>
              Men's Services
            </Typography>
            <Typography variant="body1" color="text.secondary" sx={{ mb: 3, maxWidth: '80%', mx: 'auto' }}>
              Professional grooming and styling services
            </Typography>
            <Button
              variant="contained"
              color="primary"
              size="large"
              onClick={(e) => {
                e.stopPropagation();
                handleSelect('male');
                handleProceed();
              }}
              sx={{
                mt: 1,
                px: 4,
                py: 1.5,
                borderRadius: 2,
                fontWeight: 'bold',
                textTransform: 'none',
                '&:hover': {
                  transform: 'translateY(-2px)',
                  boxShadow: 3
                },
                transition: 'all 0.3s ease-in-out'
              }}
            >
              Get Token
            </Button>
          </Paper>
        </Grid>

        {/* Female Section */}
        <Grid item xs={12} sm={6} md={5}>
          <Paper 
            elevation={3} 
            onClick={() => handleSelect('female')}
            sx={{ 
              p: 4, 
              borderRadius: 4,
              cursor: 'pointer',
              transition: 'all 0.3s ease-in-out',
              '&:hover': {
                transform: 'translateY(-5px)',
                boxShadow: 6,
                borderColor: 'secondary.main',
                borderWidth: '2px',
                borderStyle: 'solid'
              },
              bgcolor: 'rgba(255, 10, 145, 0.05)',
              height: '100%',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              textAlign: 'center',
              minHeight: 300
            }}
          >
            <ArrowBackIcon sx={{ fontSize: 80, color: 'secondary.main', mb: 2 }} />
            <Typography variant="h4" color="secondary" gutterBottom sx={{ fontWeight: 'bold' }}>
              Women's Services
            </Typography>
            <Typography variant="body1" color="text.secondary" sx={{ mb: 3, maxWidth: '80%', mx: 'auto' }}>
              Premium beauty and wellness services
            </Typography>
            <Button
              variant="contained"
              color="secondary"
              size="large"
              onClick={(e) => {
                e.stopPropagation();
                handleSelect('female');
                handleProceed();
              }}
              sx={{
                mt: 1,
                px: 4,
                py: 1.5,
                borderRadius: 2,
                fontWeight: 'bold',
                textTransform: 'none',
                '&:hover': {
                  transform: 'translateY(-2px)',
                  boxShadow: 3
                },
                transition: 'all 0.3s ease-in-out'
              }}
            >
              Get Token
            </Button>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );

  // Render selected gender view with token status
  const renderSelectedGenderView = () => {
    const isMale = selectedGender === 'male';
    const color = isMale ? 'primary' : 'secondary';

    return (
      <Box sx={{ textAlign: 'center', p: 3, maxWidth: 800, mx: 'auto' }}>
        <IconButton 
          onClick={handleBack} 
          sx={{ 
            position: 'absolute',
            left: 20,
            color: `${color}.main`,
            '&:hover': {
              backgroundColor: 'rgba(0, 0, 0, 0.04)'
            }
          }}
        >
          <ArrowBackIcon />
        </IconButton>

        <Box sx={{ mb: 4 }}>
          <ArrowBackIcon sx={{ 
            fontSize: 80, 
            color: `${color}.main`, 
            mb: 2,
            bgcolor: `${color}.50`,
            p: 1.5,
            borderRadius: '50%'
          }} />
          <Typography variant="h4" color={`${color}.main`} gutterBottom sx={{ fontWeight: 'bold' }}>
            {isMale ? "Men's" : "Women's"} Token Status
          </Typography>
        </Box>

        <Grid container spacing={3} justifyContent="center">
          {/* Current Token */}
          <Grid item xs={12} sm={6}>
            <Paper elevation={2} sx={{ 
              p: 3, 
              borderRadius: 3,
              height: '100%',
              borderLeft: `4px solid ${theme.palette[color].main}`,
              bgcolor: 'background.paper'
            }}>
              <Typography variant="subtitle1" color="text.secondary" gutterBottom>
                Currently Serving
              </Typography>
              <Typography 
                variant="h1" 
                sx={{ 
                  fontWeight: 'bold',
                  fontSize: '4rem',
                  lineHeight: 1.2,
                  mb: 1,
                  color: `${color}.main`
                }}
              >
                {isMale ? currentMaleToken : currentFemaleToken}
              </Typography>
              <Typography variant="body1" color="text.secondary">
                {isMale ? `Token M${currentMaleToken} is being served` : `Token F${currentFemaleToken} is being served`}
              </Typography>
            </Paper>
          </Grid>

          {/* User's Token */}
          <Grid item xs={12} sm={6}>
            <Paper elevation={2} sx={{ 
              p: 3, 
              borderRadius: 3,
              height: '100%',
              bgcolor: 'background.paper',
              border: `1px solid ${theme.palette.grey[200]}`
            }}>
              <Typography variant="subtitle1" color="text.secondary" gutterBottom>
                Your Token
              </Typography>

              {isMale ? recentMaleToken : recentFemaleToken ? (
                <>
                  <Typography 
                    variant="h2" 
                    sx={{ 
                      fontWeight: 'bold',
                      mb: 1,
                      color: 'text.primary'
                    }}
                  >
                    {isMale ? `M${recentMaleToken.tokenNumber}` : `F${recentFemaleToken.tokenNumber}`}
                  </Typography>
                  <Chip 
                    label={isMale ? recentMaleToken.served ? 'Served' : 'In Queue' : recentFemaleToken.served ? 'Served' : 'In Queue'} 
                    color={isMale ? recentMaleToken.served ? 'default' : 'primary' : recentFemaleToken.served ? 'default' : 'secondary'}
                    size="medium"
                    sx={{ mb: 1, fontWeight: 500 }}
                  />
                  <Typography variant="body1" color="text.secondary">
                    {isMale ? recentMaleToken.service : recentFemaleToken.service}
                  </Typography>
                </>
              ) : (
                <Box sx={{ py: 2 }}>
                  <Typography variant="h6" color="text.secondary" sx={{ mb: 2 }}>
                    No token issued yet
                  </Typography>
                  <Button
                    variant="contained"
                    color={color}
                    onClick={handleProceed}
                    size="large"
                    sx={{ mt: 1 }}
                  >
                    Get New Token
                  </Button>
                </Box>
              )}
            </Paper>
          </Grid>
        </Grid>

        {(isMale ? recentMaleToken : recentFemaleToken) && (
          <Box sx={{ mt: 4 }}>
            <Button
              variant="contained"
              color={color}
              size="large"
              onClick={handleProceed}
              sx={{ px: 6, py: 1.5, borderRadius: 2, fontWeight: 'bold' }}
            >
              Get Another Token
            </Button>
            <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
              {isMale 
                ? 'Please wait for your token number to be displayed on the screen.'
                : 'Your comfort and satisfaction are our top priorities.'}
            </Typography>
          </Box>
        )}
      </Box>
    );
  };

  // Main render
  return (
    <Layout>
      <Fade in={animate} timeout={800}>
        <Container maxWidth="lg" sx={{ py: isMobile ? 2 : 4 }}>
          <Paper elevation={3} sx={{ 
            p: { xs: 2, md: 4 }, 
            mb: 4, 
            background: 'linear-gradient(145deg, #f8f8f8 0%, #ffffff 100%)',
            borderRadius: 4
          }}>
            {selectedGender ? renderSelectedGenderView() : renderGenderSelection()}
          </Paper>
        </Container>
      </Fade>
    </Layout>
  );
};

export default Home;
