import React, { useContext, useState, useEffect } from 'react';
import { TokenContext } from '../context/TokenContext';
import { Container, Typography, Grid, Button, Paper, Box, Fade, Divider } from '@mui/material';
import { useTheme } from '@mui/material/styles';
import Layout from '../components/Layout';
import { useNavigate } from 'react-router-dom';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import PersonIcon from '@mui/icons-material/Person';

const Home = () => {
  const theme = useTheme();
  const navigate = useNavigate();
  const { getCurrentToken, getRecentToken } = useContext(TokenContext);
  const [animate, setAnimate] = useState(false);

  // Get current and recent tokens for both genders
  const currentMaleToken = getCurrentToken('male');
  const currentFemaleToken = getCurrentToken('female');
  const recentMaleToken = getRecentToken('male');
  const recentFemaleToken = getRecentToken('female');

  useEffect(() => {
    setAnimate(true);
  }, [currentMaleToken, currentFemaleToken]); // Re-run effect when current tokens change

  const handleSelect = (gender) => {
    navigate('/services', { state: { gender } });
  };

  return (
    <Layout>
      <Fade in={animate} timeout={800}>
        <Container maxWidth="md">
          <Paper elevation={3} sx={{ p: 4, mb: 6, background: 'linear-gradient(145deg, #f8f8f8 0%, #ffffff 100%)' }}>
            <Box sx={{ textAlign: 'center', mb: 4 }}>
              <Typography variant="h3" component="h1" gutterBottom sx={{ 
                fontWeight: 700,
                background: 'linear-gradient(45deg, #33126B 30%, #FF0A91 90%)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                mb: 2
              }}>
                Welcome to Purple Salon
              </Typography>
              <Typography variant="h6" color="text.secondary" paragraph>
                Experience premium grooming services
              </Typography>
            </Box>

            <Box sx={{ mb: 4, '& > :not(style)': { mb: 3 } }}>
              {/* Male Tokens */}
              <Paper elevation={3} sx={{ p: 3, borderRadius: 4, bgcolor: 'rgba(51, 18, 107, 0.1)' }}>
                <Typography variant="h5" sx={{ mb: 2, color: 'primary.main', fontWeight: 'bold' }}>
                  ♂ Male Tokens
                </Typography>
                <Box sx={{ display: 'flex', gap: 3, flexDirection: { xs: 'column', sm: 'row' } }}>
                  <Box sx={{ 
                    flex: 1,
                    bgcolor: 'primary.light',
                    color: 'primary.contrastText',
                    p: 3,
                    borderRadius: 3,
                    boxShadow: 2,
                    textAlign: 'center'
                  }}>
                    <Typography variant="h6" gutterBottom>Currently Serving</Typography>
                    <Typography variant="h2" sx={{ fontWeight: 'bold', fontSize: { xs: '3rem', sm: '4rem' } }}>
                      {currentMaleToken > 0 ? `M${currentMaleToken}` : 'N/A'}
                    </Typography>
                  </Box>

                  {recentMaleToken && (
                    <Box sx={{
                      flex: 1,
                      bgcolor: 'rgba(255, 10, 145, 0.1)',
                      p: 3,
                      borderRadius: 3,
                      boxShadow: 2,
                      textAlign: 'center',
                      border: '2px dashed',
                      borderColor: 'secondary.main'
                    }}>
                      <Typography variant="h6" gutterBottom>Last Issued</Typography>
                      <Typography variant="h2" sx={{ 
                        fontWeight: 'bold',
                        fontSize: { xs: '3rem', sm: '4rem' },
                        background: 'linear-gradient(45deg, #33126B 30%, #FF0A91 90%)',
                        WebkitBackgroundClip: 'text',
                        WebkitTextFillColor: 'transparent'
                      }}>
                        M{recentMaleToken.tokenNumber}
                      </Typography>
                      <Typography variant="body2" sx={{ mt: 1, color: 'text.secondary' }}>
                        {recentMaleToken.service}
                      </Typography>
                    </Box>
                  )}
                </Box>
              </Paper>

              {/* Female Tokens */}
              <Paper elevation={3} sx={{ p: 3, borderRadius: 4, bgcolor: 'rgba(255, 10, 145, 0.05)' }}>
                <Typography variant="h5" sx={{ mb: 2, color: 'secondary.main', fontWeight: 'bold' }}>
                  ♀ Female Tokens
                </Typography>
                <Box sx={{ display: 'flex', gap: 3, flexDirection: { xs: 'column', sm: 'row' } }}>
                  <Box sx={{ 
                    flex: 1,
                    bgcolor: 'secondary.light',
                    color: 'secondary.contrastText',
                    p: 3,
                    borderRadius: 3,
                    boxShadow: 2,
                    textAlign: 'center'
                  }}>
                    <Typography variant="h6" gutterBottom>Currently Serving</Typography>
                    <Typography variant="h2" sx={{ fontWeight: 'bold', fontSize: { xs: '3rem', sm: '4rem' } }}>
                      {currentFemaleToken > 0 ? `F${currentFemaleToken}` : 'N/A'}
                    </Typography>
                  </Box>

                  {recentFemaleToken && (
                    <Box sx={{
                      flex: 1,
                      bgcolor: 'rgba(51, 18, 107, 0.1)',
                      p: 3,
                      borderRadius: 3,
                      boxShadow: 2,
                      textAlign: 'center',
                      border: '2px dashed',
                      borderColor: 'primary.main'
                    }}>
                      <Typography variant="h6" gutterBottom>Last Issued</Typography>
                      <Typography variant="h2" sx={{ 
                        fontWeight: 'bold',
                        fontSize: { xs: '3rem', sm: '4rem' },
                        background: 'linear-gradient(45deg, #FF0A91 30%, #FF8E53 90%)',
                        WebkitBackgroundClip: 'text',
                        WebkitTextFillColor: 'transparent'
                      }}>
                        F{recentFemaleToken.tokenNumber}
                      </Typography>
                      <Typography variant="body2" sx={{ mt: 1, color: 'text.secondary' }}>
                        {recentFemaleToken.service}
                      </Typography>
                    </Box>
                  )}
                </Box>
              </Paper>
            </Box>

            <Box sx={{ textAlign: 'center', mt: 4 }}>
              <Typography variant="h6" gutterBottom>
                Please select your gender to book a service
              </Typography>
              <Box sx={{ display: 'flex', gap: 3, justifyContent: 'center', mt: 3, mb: 2 }}>
                <Button 
                  variant="contained" 
                  size="large" 
                  onClick={() => handleSelect('male')}
                  sx={{ 
                    minWidth: 150,
                    height: 60,
                    fontSize: '1.1rem',
                    '&:hover': {
                      transform: 'translateY(-3px)',
                      boxShadow: '0 4px 20px rgba(0,0,0,0.2)'
                    }
                  }}
                >
                  ♂ Male
                </Button>
                <Button 
                  variant="contained" 
                  size="large" 
                  color="secondary" 
                  onClick={() => handleSelect('female')}
                  sx={{ 
                    minWidth: 150,
                    height: 60,
                    fontSize: '1.1rem',
                    '&:hover': {
                      transform: 'translateY(-3px)',
                      boxShadow: '0 4px 20px rgba(0,0,0,0.2)'
                    }
                  }}
                >
                  ♀ Female
                </Button>
              </Box>
            </Box>
          </Paper>
        </Container>
      </Fade>
    </Layout>
  );
};

export default Home;
