import React, { useContext, useState } from 'react';
import { Container, Typography, TextField, Button, Stack, Paper } from '@mui/material';
import Layout from '../components/Layout';
import { useNavigate, useLocation } from 'react-router-dom';
import { TokenContext } from '../context/TokenContext';

const TokenForm = () => {
  const navigate = useNavigate();
  const {
    state: { gender, service }
  } = useLocation();
  const { addToken, nextTokenNumber } = useContext(TokenContext);

  const [name, setName] = useState('');
  const [mobile, setMobile] = useState('');
  const [otpSent, setOtpSent] = useState(false);
  const [otp, setOtp] = useState('');

  const mockSendOtp = () => {
    // In real app send OTP; here just simulate
    setOtpSent(true);
  };

  const handleSubmit = () => {
    // Skip OTP verification for demo
    const token = {
      tokenNumber: nextTokenNumber,
      gender,
      service,
      name,
      mobile,
      served: false
    };
    addToken(token);
    navigate('/success', { state: { tokenNumber: token.tokenNumber } });
  };

  return (
    <Layout>
    <Container maxWidth="sm" sx={{ mt: 6 }}>
      <Typography variant="h6" gutterBottom>
        Enter Details
      </Typography>
      <Stack spacing={2}>
        <TextField label="Name" value={name} onChange={(e) => setName(e.target.value)} fullWidth />
        <TextField label="Mobile" value={mobile} onChange={(e) => setMobile(e.target.value)} fullWidth />
        {!otpSent ? (
          <Button variant="outlined" onClick={mockSendOtp} disabled={!mobile}>
            Send OTP
          </Button>
        ) : (
          <TextField label="OTP" value={otp} onChange={(e) => setOtp(e.target.value)} fullWidth />
        )}
        <Button variant="contained" onClick={handleSubmit} disabled={!name || !mobile}>
          Get Token Number
        </Button>
      </Stack>
    </Container>
    </Layout>
  );
};

export default TokenForm;
