import React, { useContext, useEffect, useState } from 'react';
import { 
  Container, 
  Typography, 
  Paper, 
  Box, 
  Button, 
  List, 
  ListItem, 
  ListItemText, 
  ListItemSecondaryAction,
  Divider,
  Grid,
  CircularProgress,
  Avatar,
  Chip,
  Tooltip,
  alpha
} from '@mui/material';
import { TokenContext } from '../context/TokenContext';
import RefreshIcon from '@mui/icons-material/Refresh';
import PersonIcon from '@mui/icons-material/Person';
import PhoneIcon from '@mui/icons-material/Phone';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import PendingActionsIcon from '@mui/icons-material/PendingActions';

// Theme colors
const themeColors = {
  primary: '#33126B',
  secondary: '#FF0A91',
  background: '#FAFAFA',
  textPrimary: '#212121',
  textSecondary: '#757575',
  success: '#4caf50',
  warning: '#ff9800',
  error: '#f44336',
};

const AdminPanel = () => {
  const { 
    tokens, 
    loading, 
    serveToken, 
    currentMaleToken,
    currentFemaleToken,
    fetchTokens
  } = useContext(TokenContext);
  const [refreshing, setRefreshing] = useState(false);

  // Fetch tokens on component mount
  useEffect(() => {
    const loadTokens = async () => {
      setRefreshing(true);
      try {
        await Promise.all([
          fetchTokens('male'),
          fetchTokens('female')
        ]);
      } catch (error) {
        console.error('Error loading tokens:', error);
      } finally {
        setRefreshing(false);
      }
    };

    loadTokens();
  }, [fetchTokens]);

  const handleServeToken = async (tokenId, gender) => {
    try {
      await serveToken(tokenId, gender);
      // Refresh the tokens after serving
      await fetchTokens(gender);
    } catch (error) {
      console.error('Error serving token:', error);
    }
  };

  const renderTokenList = (gender) => {
    const tokenList = tokens[gender] || [];
    const currentToken = gender === 'male' ? currentMaleToken : currentFemaleToken;
    const isLoading = loading || refreshing;

    return (
      <Grid item xs={12} md={6} key={gender}>
        <Paper sx={{ p: 2, height: '100%' }}>
          <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
            <Typography variant="h6">
              {gender === 'male' ? 'Male' : 'Female'} Queue
            </Typography>
            {isLoading && <CircularProgress size={24} />}
          </Box>

          {currentToken && (
            <Box sx={{ mb: 3, p: 2, bgcolor: 'action.hover', borderRadius: 1 }}>
              <Typography variant="subtitle1">
                Now Serving: <strong>Token {currentToken.token_number}</strong>
              </Typography>
              <Typography variant="body2">
                {currentToken.name} • {currentToken.service}
              </Typography>
              <Typography variant="caption" display="block">
                {currentToken.phone}
              </Typography>
            </Box>
          )}

          <List dense>
            {tokenList.map((token) => (
              <React.Fragment key={token.id}>
                <ListItem 
                  secondaryAction={
                    <Button
                      variant="contained"
                      size="small"
                      onClick={() => handleServeToken(token.id, gender)}
                      disabled={token.status === 'served' || refreshing}
                    >
                      {token.status === 'served' ? 'Serving' : 'Serve'}
                    </Button>
                  }
                >
                  <ListItemText
                    primary={`Token ${token.token_number}`}
                    secondary={`${token.name} • ${token.service}`}
                  />
                </ListItem>
                <Divider />
              </React.Fragment>
            ))}

            {!isLoading && tokenList.length === 0 && (
              <Typography variant="body2" color="text.secondary" sx={{ p: 2, textAlign: 'center' }}>
                No tokens in queue
              </Typography>
            )}
          </List>
        </Paper>
      </Grid>
    );
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h4" component="h1">
          Admin Dashboard
        </Typography>
        <Button 
          variant="outlined" 
          onClick={() => {
            fetchTokens('male');
            fetchTokens('female');
          }}
          disabled={refreshing}
        >
          Refresh
        </Button>
      </Box>

      <Grid container spacing={3}>
        {renderTokenList('male')}
        {renderTokenList('female')}
      </Grid>
    </Container>
  );
};

export default AdminPanel;
