import React, { useContext } from 'react';
import { Container, Typography, Table, TableHead, TableRow, TableCell, TableBody, Button } from '@mui/material';
import { TokenContext } from '../context/TokenContext';

const AdminPanel = () => {
  const { tokens, serveToken } = useContext(TokenContext);

  return (
    <Container sx={{ mt: 6 }}>
      <Typography variant="h5" gutterBottom textAlign="center">
        Admin Panel
      </Typography>

      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Token</TableCell>
            <TableCell>Name</TableCell>
            <TableCell>Service</TableCell>
            <TableCell>Mobile</TableCell>
            <TableCell>Status</TableCell>
            <TableCell>Action</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {tokens.map((t) => (
            <TableRow key={t.tokenNumber} sx={{ bgcolor: t.served ? 'grey.100' : 'inherit' }}>
              <TableCell>{t.tokenNumber}</TableCell>
              <TableCell>{t.name}</TableCell>
              <TableCell>{t.service}</TableCell>
              <TableCell>{t.mobile}</TableCell>
              <TableCell>{t.served ? 'Served' : 'Waiting'}</TableCell>
              <TableCell>
                {!t.served && (
                  <Button variant="outlined" size="small" onClick={() => serveToken(t.tokenNumber)}>
                    Serve
                  </Button>
                )}
              </TableCell>
            </TableRow>
          ))}
          {tokens.length === 0 && (
            <TableRow>
              <TableCell colSpan={6} align="center">
                No tokens yet
              </TableCell>
            </TableRow>
          )}
        </TableBody>
      </Table>
    </Container>
  );
};

export default AdminPanel;
