import React, { createContext, useState } from 'react';

export const TokenContext = createContext();

export const TokenProvider = ({ children }) => {
  const [tokens, setTokens] = useState([]); // {tokenNumber, gender, service, name, mobile, served}
  const [currentToken, setCurrentToken] = useState(null);
  const [nextTokenNumber, setNextTokenNumber] = useState(1);

  const addToken = (token) => {
    setTokens((prev) => [...prev, token]);
    setNextTokenNumber((n) => n + 1);
  };

  const serveToken = (tokenNumber) => {
    setTokens((prev) => prev.map((t) => (t.tokenNumber === tokenNumber ? { ...t, served: true } : t)));
    setCurrentToken(tokenNumber);
  };

  return (
    <TokenContext.Provider value={{ tokens, currentToken, nextTokenNumber, addToken, serveToken }}>
      {children}
    </TokenContext.Provider>
  );
};
