import React from 'react';
import { Container, Typography, Grid, Button, Paper } from '@mui/material';
import Layout from '../components/Layout';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const navigate = useNavigate();
  const handleSelect = (gender) => {
    navigate('/services', { state: { gender } });
  };

  return (
    <Layout>
    <Container maxWidth="sm" sx={{ textAlign: 'center', mt: 8 }}>
      <Typography variant="h4" gutterBottom color="primary.main">
        Welcome to Purple Salon
      </Typography>
      <Typography variant="subtitle1" gutterBottom>
        Please select your gender to continue
      </Typography>
      <Grid container spacing={2} justifyContent="center" sx={{ mt: 4 }}>
        <Grid item>
          <Button variant="contained" size="large" onClick={() => handleSelect('male')}>
            Male
          </Button>
        </Grid>
        <Grid item>
          <Button variant="contained" size="large" color="secondary" onClick={() => handleSelect('female')}>
            Female
          </Button>
        </Grid>
      </Grid>
    </Container>
    </Layout>
  );
};

export default Home;
