import React from 'react';
import { Container, Typography, Grid, Button, Paper, Box, Fade } from '@mui/material';
import { useTheme } from '@mui/material/styles';
import Layout from '../components/Layout';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const theme = useTheme();
  const navigate = useNavigate();
  const { currentToken } = useContext(TokenContext);
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    setAnimate(true);
  }, []);

  const handleSelect = (gender) => {
    navigate('/services', { state: { gender } });
  };

  return (
    <Layout>
      <Fade in={animate} timeout={800}>
        <Container maxWidth="md">
          <Paper elevation={3} sx={{ p: 4, mb: 6, background: 'linear-gradient(145deg, #f8f8f8 0%, #ffffff 100%)' }}>
            <Box sx={{ textAlign: 'center', mb: 4 }}>
              <Typography variant="h3" component="h1" gutterBottom sx={{ 
                fontWeight: 700,
                background: 'linear-gradient(45deg, #33126B 30%, #FF0A91 90%)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                mb: 2
              }}>
                Welcome to Purple Salon
              </Typography>
              <Typography variant="h6" color="text.secondary" paragraph>
                Experience premium grooming services
              </Typography>
            </Box>

            {currentToken && (
              <Box sx={{ 
                bgcolor: 'primary.light',
                color: 'primary.contrastText',
                p: 3,
                borderRadius: 4,
                mb: 4,
                boxShadow: 3
              }}>
                <Typography variant="subtitle1" gutterBottom>Currently Serving</Typography>
                <Typography variant="h2" sx={{ fontWeight: 'bold' }}>
                  Token #{currentToken}
                </Typography>
              </Box>
            )}

            <Box sx={{ textAlign: 'center', mt: 4 }}>
              <Typography variant="h6" gutterBottom>
                Please select your gender to book a service
              </Typography>
              <Box sx={{ display: 'flex', gap: 3, justifyContent: 'center', mt: 3, mb: 2 }}>
                <Button 
                  variant="contained" 
                  size="large" 
                  onClick={() => handleSelect('male')}
                  sx={{ 
                    minWidth: 150,
                    height: 60,
                    fontSize: '1.1rem',
                    '&:hover': {
                      transform: 'translateY(-3px)',
                      boxShadow: '0 4px 20px rgba(0,0,0,0.2)'
                    }
                  }}
                >
                  ♂ Male
                </Button>
                <Button 
                  variant="contained" 
                  size="large" 
                  color="secondary" 
                  onClick={() => handleSelect('female')}
                  sx={{ 
                    minWidth: 150,
                    height: 60,
                    fontSize: '1.1rem',
                    '&:hover': {
                      transform: 'translateY(-3px)',
                      boxShadow: '0 4px 20px rgba(0,0,0,0.2)'
                    }
                  }}
                >
                  ♀ Female
                </Button>
              </Box>
            </Box>
          </Paper>
        </Container>
      </Fade>
    </Layout>
  );
};

export default Home;
